import React, { useImperativeHandle } from 'react';
import { Text, View, StyleSheet, StatusBar, Image, Dimensions, ImageBackground, TextInput } from 'react-native';

import AppButton from '../components/AppButton';
import { useFonts } from 'expo-font';

const { width, height } = Dimensions.get('window');

export default function SignUpScreen ({navigation}) {
  let [fontsLoaded] = useFonts({
    'Assistant': require('../assets/fonts/Assistant/Assistant-VariableFont_wght.ttf'),
    'Assistant-Regular': require('../assets/fonts/Assistant/static/Assistant-Regular.ttf'),
    'Assistant-SemiBold': require('../assets/fonts/Assistant/static/Assistant-SemiBold.ttf'),
  });

  const [userInfo, setUserInfo] = React.useState({
    firstName: '',
    email: '',
    password: '',
    passwordConfirm: ''
  })

  const firstNameInputChange = (val) => {
    if (val.length !== 0) {
      setUserInfo({
        ...userInfo,
        firstName: val
      })
    } else {
      setUserInfo({
        ...userInfo,
        firstName: ""
      })
    }
  }

  const emailInputChange = (val) => {
    if (val.length !== 0) {
      setUserInfo({
        ...userInfo,
        email: val
      })
    } else {
      setUserInfo({
        ...userInfo,
        email: ""
      })
    }
  }

  const handlePasswordChange = (val) => {
    if (val.length !== 0) {
      setUserInfo({
        ...userInfo,
        password: val
      })
    } else {
      setUserInfo({
        ...userInfo,
        password: ""
      })
    }
  }

  const handlePasswordConfirmChange = (val) => {
    if (val.length !== 0) {
      setUserInfo({
        ...userInfo,
        passwordConfirm: val
      })
    } else {
      setUserInfo({
        ...userInfo,
        passwordConfirm: ""
      })
    }
  }


  return (
    <View style={styles.container}>
      <StatusBar hidden={false}/>
        <View style={styles.footerIntro}>
          <Image source={require("../assets/memoir-logo.png")} />
          <View>
            <Text style={{fontSize: 26, color: "#717171", textAlign: "center", fontFamily: "Assistant-SemiBold", width: 247}}>Create a Free Account to Get Started</Text>
            {/* <Text>firstName: {userInfo.firstName}</Text>
            <Text>email: {userInfo.email}</Text>
            <Text>pw: {userInfo.password}</Text>
            <Text>pwAgain: {userInfo.passwordConfirm}</Text> */}
          </View>
          <View style={{height: 480, justifyContent: "space-between"}}>
            <TextInput 
              placeholder="First Name"
              style={styles.inputs} 
              autoCapitalize="none"
              autoCorrect={false}
              autoFocus={true}
              onChangeText={(val) => firstNameInputChange(val)}
            />
            <TextInput 
              placeholder="Email"
              style={styles.inputs} 
              autoCapitalize="none"
              autoCorrect={false}
              onChangeText={(val) => emailInputChange(val)}
            />
            <TextInput 
              placeholder="Password"
              secureTextEntry={true}
              style={styles.inputs} 
              autoCapitalize="none"
              autoCorrect={false}
              onChangeText={(val) => handlePasswordChange(val)}
              />
            <TextInput 
              placeholder="Confirm Password"
              secureTextEntry={true}
              style={styles.inputs} 
              autoCapitalize="none"
              autoCorrect={false}
              onChangeText={(val) => handlePasswordConfirmChange(val)}
            />

            <AppButton 
              title="Create Account" 
              buttonStyles={styles.blueButton} 
              buttonTextStyles={styles.buttonText} 
              onPress={() => navigation.navigate('SignInScreen')}
            />
          </View>
        </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  image: {
    height: height,
    width: width,
  },
  bgImage: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  body: {
    flex: 2,
    justifyContent: 'space-evenly',
    alignItems: 'center',
    // borderColor: "pink",
    // borderWidth: 2,
  },
  footerIntro: {
    fontFamily: "Assistant",
    flex: 1,
    backgroundColor: '#fff',
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    paddingVertical: 50,
    paddingHorizontal: 45,
    justifyContent: "space-evenly",
    alignItems: "center"
  },
  inputs: {
    backgroundColor: "#EAEAEA",
    height: 69,
    width: 327,
    borderRadius: 20,
    padding: 20,
    fontSize: 24,
    fontFamily: "Assistant-Regular"
  },
  blueButton: {
    backgroundColor: "#3681C7",
    height: 69,
    width: 327,
    borderRadius: 20,
    alignItems: "center",
    justifyContent: "center",
  },
  buttonText: {
    color: "#fff",
    flex: 1,
    textAlign: "center",
    fontSize: 24,
    fontFamily: "Assistant-SemiBold"
  }
});
